mapper.select:
SELECT 

FROM busi_message a

WHERE a.id = #{id}

SELECT
FROM busi_message a
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
and a.company_id = #{companyId}
ORDER BY ${page.orderBy}

SELECT 

FROM busi_message a


	AND a.company_id = #{companyId}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE busi_message SET 	
	title = #{title},
	content = #{content},
	to_user = #{toUser}
WHERE id = #{id}
